declare const _default: "\n/* Global style */\nbody {\n  margin: 0px;\n  font-family: 'Inter', -apple-system, BlinkMacSystemFont, \"Segoe UI\", Roboto, Oxygen-Sans, Ubuntu, Cantarell, \"Helvetica Neue\", sans-serif;\n  font-size: 14px;\n}\n\n/* Buttons */\n.btn {\n  min-width: 100px;\n  height: 40px;\n  border-radius: 8px;\n  border: none;\n  font-weight: 600;\n  padding: 6px 15px;\n  background-color: #F4F3F8;\n  color: #333334;\n  cursor: pointer;\n  font-size: 13px;\n  transition: all .15s ease;\n}\n.btn:hover {\n  background-color: #efeff4;\n}\n\n.btn-secondary {\n  background-color: #E2ECFE;\n  color: #2a5ee4;\n}\n.btn-secondary:hover {\n  background-color: #E2ECFE;\n  color: #214dbe;\n}\n\n.btn-primary {\n  background-color: #2a5ee4;\n  color: #fff;\n}\n.btn-primary:hover {\n  background-color: #214dbe;\n}\n\n.btn-red {\n  background-color: #f8eded;\n  color: #e54538;\n}\n.btn-red:hover {\n  background-color: #f4e3e2;\n  color: #e54538;\n}\n\n.btn-green {\n  background-color: #dff6d6;\n  color: #57b158;\n}\n.btn-green:hover {\n  background-color: #c7f3b8;\n}\n\n.btn-small {\n  height: auto;\n  width: auto;\n  border-radius: 4px;\n}\n\n/* Forms */\n.form {\n  display: grid;\n  grid-gap: 24px;\n}\n\n.input {\n  border-width: 1px;\n  border-color: #e5e5ea;\n  background-color: #fff;\n  color: #1c1c1e;\n  width: 100%;\n  border-radius: 8px;\n  padding-left: 12px;\n  padding-right: 12px;\n  font-size: 14px;\n  box-sizing: border-box;\n  box-shadow: none;\n  outline: none;\n  height: 40px;\n  border-style: solid;\n}\n.input:focus {\n  border-color: #2a5ee4;\n}\ntextarea.input {\n  padding: 12px;\n}\n\n/* Texts */\n.label {\n  display: inline-block;\n  margin-bottom: 0.25rem;\n  font-weight: 500;\n  color: #1c1c1e;\n}\n.value {\n  color: #515054;\n  margin-bottom: 5px;\n}\n";
